// Set YouTube URL here - Edit this line with your YouTube URL
const youtubeUrl = 'https://www.youtube.com/watch?v=dQw4w9WgXcQ'; // CHANGE THIS URL

if (!youtubeUrl || youtubeUrl === 'https://www.youtube.com/watch?v=dQw4w9WgXcQ') {
  throw new Error('‚ö†Ô∏è Please edit the "Set YouTube URL" node and change the youtubeUrl variable to your desired YouTube video URL');
}

return {
  json: {
    youtubeUrl: youtubeUrl,
    inputMethod: 'hardcoded',
    timestamp: new Date().toISOString()
  }
};

// Extract YouTube video ID from various URL formats
const inputData = $input.first().json;
const youtubeUrl = inputData.youtubeUrl || inputData.url || inputData.YouTube_URL || '';

if (!youtubeUrl) {
  throw new Error('Please provide a YouTube URL in the youtubeUrl field');
}

let videoId = null;

// Extract video ID from various YouTube URL formats
const patterns = [
  /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([^&\n?#]+)/,
  /^([a-zA-Z0-9_-]{11})$/ // Direct video ID
];

for (const pattern of patterns) {
  const match = youtubeUrl.match(pattern);
  if (match) {
    videoId = match[1];
    break;
  }
}

if (!videoId) {
  throw new Error(`Invalid YouTube URL format: ${youtubeUrl}`);
}

console.log(`Extracted video ID: ${videoId} from URL: ${youtubeUrl}`);

return {
  json: {
    originalUrl: youtubeUrl,
    videoId: videoId
  }
};

// Process transcript array and prepare for Google Sheets
const transcriptData = $input.first().json;
const videoInfo = $('Extract Video ID').first().json;

console.log('Processing transcript data for Google Sheets...');

let transcriptItems = [];

// Handle different transcript response formats
if (transcriptData && !transcriptData.error) {
  let transcriptArray = [];

  // Extract transcript array from various possible formats
  if (Array.isArray(transcriptData.transcript)) {
    transcriptArray = transcriptData.transcript;
  } else if (Array.isArray(transcriptData.data)) {
    transcriptArray = transcriptData.data;
  } else if (Array.isArray(transcriptData)) {
    transcriptArray = transcriptData;
  } else if (transcriptData.transcript && typeof transcriptData.transcript === 'string') {
    // If transcript is a single string, split it into sentences
    transcriptArray = transcriptData.transcript.split('. ').map((text, index) => ({
      text: text + (index < transcriptData.transcript.split('. ').length - 1 ? '.' : ''),
      start: index * 5, // Estimated timing
      duration: 5
    }));
  }

  // Process each transcript item
  transcriptArray.forEach((item, index) => {
    transcriptItems.push({
      json: {
        videoId: videoInfo.videoId,
        originalUrl: videoInfo.originalUrl,
        segmentIndex: index + 1,
        text: item.text || item.content || item,
        startTime: item.start || item.startTime || item.timestamp || (index * 5),
        duration: item.duration || item.dur || 5,
        endTime: (item.start || item.startTime || (index * 5)) + (item.duration || item.dur || 5),
        extractedAt: new Date().toISOString()
      }
    });
  });

  console.log(`Processed ${transcriptItems.length} transcript segments`);
} else {
  console.warn('No valid transcript data found');
  // Create a single item indicating no transcript available
  transcriptItems.push({
    json: {
      videoId: videoInfo.videoId,
      originalUrl: videoInfo.originalUrl,
      segmentIndex: 1,
      text: 'Transcript not available',
      startTime: 0,
      duration: 0,
      endTime: 0,
      extractedAt: new Date().toISOString()
    }
  });
}

return transcriptItems;

