// Comprehensive health analysis
const now = new Date();
const healthReport = {
  timestamp: now.toISOString(),
  services: {
    youtubeAPI: { status: 'unknown', responseTime: null, error: null },
    googleSheets: { status: 'unknown', responseTime: null, error: null }
  },
  dataMetrics: {
    totalItems: 0,
    completedItems: 0,
    errorItems: 0,
    stuckItems: 0,
    recentlyUpdated: 0
  },
  alerts: [],
  recommendations: [],
  overallHealth: 'unknown'
};

// Analyze YouTube API health
try {
  const youtubeTest = $('Test YouTube API').item.json;
  if (youtubeTest && !youtubeTest.error && youtubeTest.title) {
    healthReport.services.youtubeAPI.status = 'healthy';
    healthReport.services.youtubeAPI.responseTime = '< 10s';
  } else {
    healthReport.services.youtubeAPI.status = 'unhealthy';
    healthReport.services.youtubeAPI.error = youtubeTest.error || 'API call failed';
    healthReport.alerts.push({
      severity: 'high',
      service: 'youtubeAPI',
      message: 'YouTube API is not responding correctly',
      action: 'Check API quotas and service status'
    });
  }
} catch (error) {
  healthReport.services.youtubeAPI.status = 'error';
  healthReport.services.youtubeAPI.error = error.message;
  healthReport.alerts.push({
    severity: 'critical',
    service: 'youtubeAPI',
    message: 'YouTube API test failed completely',
    action: 'Immediate investigation required'
  });
}

// Analyze Google Sheets health and data metrics
try {
  const sheetData = $('Health Scan Sheet').all();
  healthReport.services.googleSheets.status = 'healthy';

  const oneHourAgo = new Date(now.getTime() - 60 * 60 * 1000);
  const oneDayAgo = new Date(now.getTime() - 24 * 60 * 60 * 1000);

  for (const item of sheetData) {
    const data = item.json;
    const status = data.Status;
    const errorStatus = data.Error_Status;
    const lastUpdated = data.Last_Updated ? new Date(data.Last_Updated) : null;

    healthReport.dataMetrics.totalItems++;

    // Count by status
    if (status === 'Done') {
      healthReport.dataMetrics.completedItems++;
    } else if (errorStatus && errorStatus !== 'OK') {
      healthReport.dataMetrics.errorItems++;
    }

    // Check for stuck items (processing for > 1 hour)
    if (status === 'Processing' && lastUpdated && (now - lastUpdated) > 60 * 60 * 1000) {
      healthReport.dataMetrics.stuckItems++;
    }

    // Count recently updated items (< 1 hour)
    if (lastUpdated && lastUpdated > oneHourAgo) {
      healthReport.dataMetrics.recentlyUpdated++;
    }
  }

  // Generate data-based alerts
  const completionRate = healthReport.dataMetrics.totalItems > 0 
    ? (healthReport.dataMetrics.completedItems / healthReport.dataMetrics.totalItems) * 100 
    : 0;

  const errorRate = healthReport.dataMetrics.totalItems > 0 
    ? (healthReport.dataMetrics.errorItems / healthReport.dataMetrics.totalItems) * 100 
    : 0;

  if (completionRate < 70) {
    healthReport.alerts.push({
      severity: 'medium',
      service: 'processing',
      message: `Low completion rate: ${Math.round(completionRate)}%`,
      action: 'Review processing workflow for bottlenecks'
    });
  }

  if (errorRate > 20) {
    healthReport.alerts.push({
      severity: 'high',
      service: 'processing',
      message: `High error rate: ${Math.round(errorRate)}%`,
      action: 'Investigate recurring errors and improve error handling'
    });
  }

  if (healthReport.dataMetrics.stuckItems > 0) {
    healthReport.alerts.push({
      severity: 'high',
      service: 'processing',
      message: `${healthReport.dataMetrics.stuckItems} items stuck in processing`,
      action: 'Run recovery workflow to reset stuck items'
    });
  }

  if (healthReport.dataMetrics.recentlyUpdated === 0 && healthReport.dataMetrics.totalItems > healthReport.dataMetrics.completedItems) {
    healthReport.alerts.push({
      severity: 'medium',
      service: 'processing',
      message: 'No recent processing activity detected',
      action: 'Check if main workflow is running properly'
    });
  }

} catch (error) {
  healthReport.services.googleSheets.status = 'error';
  healthReport.services.googleSheets.error = error.message;
  healthReport.alerts.push({
    severity: 'critical',
    service: 'googleSheets',
    message: 'Cannot access Google Sheets data',
    action: 'Check credentials and sheet permissions'
  });
}

// Generate recommendations
if (healthReport.services.youtubeAPI.status === 'healthy' && healthReport.dataMetrics.errorRate > 10) {
  healthReport.recommendations.push('Consider implementing more aggressive retry strategies');
}

if (healthReport.dataMetrics.completedItems > 100 && healthReport.dataMetrics.errorItems < 5) {
  healthReport.recommendations.push('System is performing well - consider scaling up processing');
}

if (healthReport.alerts.length === 0) {
  healthReport.recommendations.push('All systems healthy - no immediate action required');
}

// Determine overall health
const criticalAlerts = healthReport.alerts.filter(a => a.severity === 'critical').length;
const highAlerts = healthReport.alerts.filter(a => a.severity === 'high').length;
const mediumAlerts = healthReport.alerts.filter(a => a.severity === 'medium').length;

if (criticalAlerts > 0) {
  healthReport.overallHealth = 'critical';
} else if (highAlerts > 1) {
  healthReport.overallHealth = 'degraded';
} else if (highAlerts > 0 || mediumAlerts > 2) {
  healthReport.overallHealth = 'warning';
} else {
  healthReport.overallHealth = 'healthy';
}

console.log(`Health check complete. Overall status: ${healthReport.overallHealth}, Alerts: ${healthReport.alerts.length}`);

return [{ json: healthReport }];

// Log health check results for trending analysis
const healthData = $input.item.json;

// Create a simplified log entry
const logEntry = {
  timestamp: healthData.timestamp,
  overallHealth: healthData.overallHealth,
  alertCount: healthData.alerts.length,
  youtubeAPIStatus: healthData.services.youtubeAPI.status,
  googleSheetsStatus: healthData.services.googleSheets.status,
  totalItems: healthData.dataMetrics.totalItems,
  completedItems: healthData.dataMetrics.completedItems,
  errorItems: healthData.dataMetrics.errorItems,
  stuckItems: healthData.dataMetrics.stuckItems,
  completionRate: healthData.dataMetrics.totalItems > 0 
    ? Math.round((healthData.dataMetrics.completedItems / healthData.dataMetrics.totalItems) * 100) 
    : 0,
  errorRate: healthData.dataMetrics.totalItems > 0 
    ? Math.round((healthData.dataMetrics.errorItems / healthData.dataMetrics.totalItems) * 100) 
    : 0
};

console.log('Health Check Summary:', JSON.stringify(logEntry, null, 2));

return [{
  json: {
    ...healthData,
    logEntry: logEntry,
    healthCheckComplete: true
  }
}];

// Generate daily health summary (only during business hours)
const now = new Date();
const hour = now.getHours();
const isBusinessHours = hour >= 9 && hour <= 17;
const isTopOfHour = now.getMinutes() === 0 || now.getMinutes() === 30;

if (isBusinessHours && isTopOfHour) {
  const healthData = $input.item.json;

  return [{
    json: {
      ...healthData,
      shouldSendSummary: true,
      summaryType: 'hourly_business_hours'
    }
  }];
} else {
  return [{
    json: {
      shouldSendSummary: false,
      reason: `Outside business hours or not top of hour (${hour}:${now.getMinutes()})`
    }
  }];
}
