// Initialize error tracking and recovery state
const initData = {
  workflowStartTime: new Date().toISOString(),
  retryAttempts: {
    youtubeAPI: 0,
    transcript: 0,
    aiService: 0,
    googleSheets: 0
  },
  maxRetries: {
    youtubeAPI: 3,
    transcript: 5,
    aiService: 3,
    googleSheets: 4
  },
  backoffDelays: {
    youtubeAPI: [2, 5, 10], // seconds
    transcript: [3, 8, 15, 30, 60],
    aiService: [5, 12, 25],
    googleSheets: [1, 3, 7, 15]
  },
  errorLog: [],
  recoveryMode: false,
  lastSuccessfulStep: null
};

return [{ json: initData }];

// Handle Google Sheets errors with retry logic
const errorState = $('Initialize Error State').item.json;
const sheetsError = $input.item.json;

// Increment retry counter
errorState.retryAttempts.googleSheets++;

// Log the error
const errorDetails = {
  timestamp: new Date().toISOString(),
  service: 'googleSheets',
  operation: 'read',
  error: sheetsError.error || 'Unknown sheets error',
  attemptNumber: errorState.retryAttempts.googleSheets,
  httpCode: sheetsError.httpCode || null
};

errorState.errorLog.push(errorDetails);

// Check if we should retry
if (errorState.retryAttempts.googleSheets <= errorState.maxRetries.googleSheets) {
  const delayIndex = Math.min(errorState.retryAttempts.googleSheets - 1, errorState.backoffDelays.googleSheets.length - 1);
  const delaySeconds = errorState.backoffDelays.googleSheets[delayIndex];

  console.log(`Google Sheets error (attempt ${errorState.retryAttempts.googleSheets}/${errorState.maxRetries.googleSheets}). Retrying in ${delaySeconds}s`);

  return [{
    json: {
      ...errorState,
      shouldRetry: true,
      retryDelay: delaySeconds,
      currentError: errorDetails,
      nextAction: 'retry_sheets_read'
    }
  }];
} else {
  // Max retries exceeded - trigger fallback
  console.log('Google Sheets max retries exceeded. Triggering fallback strategy.');

  return [{
    json: {
      ...errorState,
      shouldRetry: false,
      fallbackRequired: true,
      failedService: 'googleSheets',
      currentError: errorDetails,
      nextAction: 'fallback_sheets'
    }
  }];
}

// Extract video ID and prepare for processing with error handling
const items = [];
const errorState = $('Initialize Error State').item.json;
let todoCount = 0;

// Safely get sheet data
let sheetData = [];
try {
  sheetData = $('Read Google Sheet (Safe)').all();
} catch (error) {
  console.log('Error getting sheet data:', error.message);
  return [{
    json: {
      ...errorState,
      error: 'Failed to access sheet data',
      nextAction: 'error_notification'
    }
  }];
}

// Count all items that need processing
for (const item of sheetData) {
  const status = item.json['Status'];
  const errorStatus = item.json['Error_Status'];

  // Process items that are not 'Done' or have recoverable errors
  if (status !== 'Done' && errorStatus !== 'FATAL') {
    todoCount++;
  }
}

// Find first item to process (prioritize error recovery)
for (const item of sheetData) {
  const youtubeUrl = item.json['YouTube URL'];
  const status = item.json['Status'];
  const errorStatus = item.json['Error_Status'];
  const lastError = item.json['Last_Error'];

  // Skip if already processed or has fatal error
  if (status === 'Done' || errorStatus === 'FATAL') {
    continue;
  }

  let videoId = null;

  if (youtubeUrl) {
    // Extract video ID from various YouTube URL formats
    const patterns = [
      /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([^&\n?#]+)/,
      /^([a-zA-Z0-9_-]{11})$/ // Direct video ID
    ];

    for (const pattern of patterns) {
      const match = youtubeUrl.match(pattern);
      if (match) {
        videoId = match[1];
        break;
      }
    }
  }

  if (videoId) {
    items.push({
      json: {
        ...item.json,
        ...errorState,
        videoId: videoId,
        rowIndex: item.json.__rowIndex || items.length + 2,
        totalTodoCount: todoCount,
        isRecovery: errorStatus === 'RETRYING',
        lastError: lastError || null,
        lastSuccessfulStep: 'extract_video_id'
      }
    });

    // Process only first item, return for next iteration
    break;
  }
}

// If no items to process, return completion signal
if (items.length === 0) {
  console.log('No more items to process - all done!');
  return [{
    json: {
      ...errorState,
      allComplete: true,
      totalTodoCount: 0,
      message: 'All items processed!',
      lastSuccessfulStep: 'workflow_complete'
    }
  }];
}

console.log(`Found ${todoCount} items to process, processing first item (Recovery: ${items[0].json.isRecovery})`);
return items;

// Handle YouTube API errors with intelligent retry logic
const currentData = $input.item.json;
const youtubeError = $('Get Video Data (Safe)').item.json;

// Increment retry counter
currentData.retryAttempts.youtubeAPI++;

// Analyze error type
let errorType = 'unknown';
let isRetryable = true;
let customDelay = null;

if (youtubeError.statusCode) {
  switch (youtubeError.statusCode) {
    case 404:
      errorType = 'video_not_found';
      isRetryable = false; // Don't retry 404s
      break;
    case 429:
      errorType = 'rate_limit';
      customDelay = 60; // Wait 1 minute for rate limits
      break;
    case 403:
      errorType = 'forbidden';
      isRetryable = false;
      break;
    case 500:
    case 502:
    case 503:
    case 504:
      errorType = 'server_error';
      break;
    default:
      errorType = `http_${youtubeError.statusCode}`;
  }
}

// Log the error
const errorDetails = {
  timestamp: new Date().toISOString(),
  service: 'youtubeAPI',
  operation: 'oembed',
  error: youtubeError.error || `HTTP ${youtubeError.statusCode}`,
  errorType: errorType,
  attemptNumber: currentData.retryAttempts.youtubeAPI,
  videoId: currentData.videoId,
  isRetryable: isRetryable
};

currentData.errorLog.push(errorDetails);

// Determine next action
if (!isRetryable) {
  // Use fallback strategy for non-retryable errors
  console.log(`YouTube API non-retryable error (${errorType}). Using fallback strategy.`);

  return [{
    json: {
      ...currentData,
      shouldRetry: false,
      useFallback: true,
      fallbackReason: errorType,
      currentError: errorDetails,
      nextAction: 'fallback_youtube_data'
    }
  }];
} else if (currentData.retryAttempts.youtubeAPI <= currentData.maxRetries.youtubeAPI) {
  // Calculate retry delay
  const delayIndex = Math.min(currentData.retryAttempts.youtubeAPI - 1, currentData.backoffDelays.youtubeAPI.length - 1);
  let delaySeconds = customDelay || currentData.backoffDelays.youtubeAPI[delayIndex];

  // Add jitter to prevent thundering herd
  delaySeconds += Math.random() * 3;

  console.log(`YouTube API error (${errorType}, attempt ${currentData.retryAttempts.youtubeAPI}/${currentData.maxRetries.youtubeAPI}). Retrying in ${Math.round(delaySeconds)}s`);

  return [{
    json: {
      ...currentData,
      shouldRetry: true,
      retryDelay: Math.round(delaySeconds),
      currentError: errorDetails,
      nextAction: 'retry_youtube_api'
    }
  }];
} else {
  // Max retries exceeded - use fallback
  console.log('YouTube API max retries exceeded. Using fallback strategy.');

  return [{
    json: {
      ...currentData,
      shouldRetry: false,
      useFallback: true,
      fallbackReason: 'max_retries_exceeded',
      currentError: errorDetails,
      nextAction: 'fallback_youtube_data'
    }
  }];
}

// Fallback strategy for YouTube data when API fails
const currentData = $input.item.json;

console.log(`Using YouTube fallback strategy for video ${currentData.videoId}`);

// Generate fallback data
const fallbackTitle = `Video ${currentData.videoId} (API Unavailable)`;
const fallbackDescription = `Content processing for video ID: ${currentData.videoId}. Original API error: ${currentData.currentError?.error || 'Unknown error'}`;
const fallbackThumbnail = `https://img.youtube.com/vi/${currentData.videoId}/maxresdefault.jpg`;

// Log fallback usage
const fallbackLog = {
  timestamp: new Date().toISOString(),
  service: 'fallback',
  operation: 'youtube_data_fallback',
  reason: currentData.fallbackReason,
  videoId: currentData.videoId
};

currentData.errorLog.push(fallbackLog);

return [{
  json: {
    ...currentData,
    videoTitle: fallbackTitle,
    videoDescription: fallbackDescription,
    videoThumbnail: fallbackThumbnail,
    dataSource: 'fallback',
    lastSuccessfulStep: 'fallback_youtube_data',
    nextAction: 'process_video_data'
  }
}];

// Process video data safely with error handling
let currentData = $input.item.json;

// Determine data source
let videoData = null;
let dataSource = 'fallback';

// Try to get successful YouTube API data first
try {
  const youtubeNode = $('Get Video Data (Safe)');
  const youtubeResponse = youtubeNode?.item?.json;

  if (youtubeResponse && !youtubeResponse.error && youtubeResponse.body) {
    videoData = typeof youtubeResponse.body === 'string' 
      ? JSON.parse(youtubeResponse.body) 
      : youtubeResponse.body;
    dataSource = 'youtube_api';
  } else if (youtubeResponse && !youtubeResponse.error && youtubeResponse.title) {
    videoData = youtubeResponse;
    dataSource = 'youtube_api';
  }
} catch (error) {
  console.log('Could not process YouTube API response:', error.message);
}

// Extract title, description, and thumbnail
let title, description, thumbnail;

if (dataSource === 'youtube_api' && videoData) {
  title = videoData.title || `Video ${currentData.videoId}`;
  description = videoData.author_name 
    ? `Video by ${videoData.author_name}: ${videoData.title || 'No title available'}` 
    : `Video content: ${videoData.title || 'No title available'}`;
  thumbnail = `https://img.youtube.com/vi/${currentData.videoId}/maxresdefault.jpg`;

  console.log('Successfully processed YouTube API data');
} else {
  // Use fallback data
  title = currentData.videoTitle || `Video ${currentData.videoId} (Fallback)`;
  description = currentData.videoDescription || 'Description not available due to API issues';
  thumbnail = currentData.videoThumbnail || `https://img.youtube.com/vi/${currentData.videoId}/maxresdefault.jpg`;

  console.log('Using fallback video data');
}

// Calculate remaining items
const remainingItems = currentData.totalTodoCount - 1;

// Update success tracking
currentData.lastSuccessfulStep = 'process_video_data';

// Reset YouTube retry counter on success
if (dataSource === 'youtube_api') {
  currentData.retryAttempts.youtubeAPI = 0;
}

return [{
  json: {
    ...currentData,
    rowIndex: currentData.rowIndex,
    YouTubeURL: currentData['YouTube URL'],
    Title: title,
    Description: description,
    Thumbnail: thumbnail,
    Status: 'Processing',
    Error_Status: dataSource === 'fallback' ? 'FALLBACK_USED' : 'OK',
    Data_Source: dataSource,
    processedAt: new Date().toISOString(),
    remainingItems: remainingItems
  }
}];

// Handle Google Sheets update errors
const currentData = $('Process Video Data (Safe)').item.json;
const updateError = $input.item.json;

// Increment retry counter
currentData.retryAttempts.googleSheets++;

// Log the error
const errorDetails = {
  timestamp: new Date().toISOString(),
  service: 'googleSheets',
  operation: 'update',
  error: updateError.error || 'Unknown update error',
  attemptNumber: currentData.retryAttempts.googleSheets,
  videoId: currentData.videoId
};

currentData.errorLog.push(errorDetails);

// Check if we should retry
if (currentData.retryAttempts.googleSheets <= currentData.maxRetries.googleSheets) {
  const delayIndex = Math.min(currentData.retryAttempts.googleSheets - 1, currentData.backoffDelays.googleSheets.length - 1);
  const delaySeconds = currentData.backoffDelays.googleSheets[delayIndex];

  console.log(`Google Sheets update error (attempt ${currentData.retryAttempts.googleSheets}/${currentData.maxRetries.googleSheets}). Retrying in ${delaySeconds}s`);

  return [{
    json: {
      ...currentData,
      shouldRetryUpdate: true,
      retryDelay: delaySeconds,
      currentError: errorDetails,
      nextAction: 'retry_sheets_update'
    }
  }];
} else {
  // Max retries exceeded - log error but continue processing
  console.log('Google Sheets update max retries exceeded. Continuing with next item.');

  // Mark item with error status for manual review
  return [{
    json: {
      ...currentData,
      shouldRetryUpdate: false,
      updateFailed: true,
      Status: 'Processing_Error',
      Error_Status: 'UPDATE_FAILED',
      currentError: errorDetails,
      nextAction: 'continue_processing'
    }
  }];
}

// Check remaining items and prepare for next iteration
let currentData = $input.item.json;

// Try to re-read sheet to get current status
let sheetData = [];
try {
  sheetData = $('Read Google Sheet (Safe)').all();
} catch (error) {
  console.log('Could not re-read sheet for remaining count, using estimate');
  // Use estimated count
  const remainingItems = Math.max(0, currentData.remainingItems || 0);

  return [{
    json: {
      ...currentData,
      shouldContinue: remainingItems > 0,
      remainingItems: remainingItems,
      message: remainingItems > 0 
        ? `Estimated ${remainingItems} items remaining (sheet read failed)` 
        : 'Processing complete (estimated)',
      lastSuccessfulStep: 'check_remaining_items'
    }
  }];
}

// Count items that still need processing
let todoCount = 0;
for (const item of sheetData) {
  const status = item.json['Status'];
  const errorStatus = item.json['Error_Status'];

  // Count items that are not 'Done' and don't have fatal errors
  if (status !== 'Done' && errorStatus !== 'FATAL') {
    todoCount++;
  }
}

console.log(`Items still to process: ${todoCount}`);

// Reset retry counters on successful sheet read
currentData.retryAttempts.googleSheets = 0;
currentData.lastSuccessfulStep = 'check_remaining_items';

if (todoCount > 0) {
  // More items to process
  return [{
    json: {
      ...currentData,
      shouldContinue: true,
      remainingItems: todoCount,
      message: `${todoCount} items remaining, continuing loop...`
    }
  }];
} else {
  // All items processed
  return [{
    json: {
      ...currentData,
      shouldContinue: false,
      remainingItems: 0,
      message: 'All items processed! Loop complete.',
      workflowEndTime: new Date().toISOString()
    }
  }];
}

// Generate comprehensive completion report with error summary
const finalData = $input.first().json;

// Calculate processing statistics
const workflowDuration = finalData.workflowEndTime 
  ? (new Date(finalData.workflowEndTime) - new Date(finalData.workflowStartTime)) / 1000
  : 0;

// Categorize errors
const errorSummary = {
  total: finalData.errorLog.length,
  byService: {},
  byType: {},
  retryStats: finalData.retryAttempts
};

for (const error of finalData.errorLog) {
  // Count by service
  errorSummary.byService[error.service] = (errorSummary.byService[error.service] || 0) + 1;

  // Count by error type
  const errorType = error.errorType || error.operation;
  errorSummary.byType[errorType] = (errorSummary.byType[errorType] || 0) + 1;
}

const completionReport = {
  status: 'completed',
  message: finalData.message || 'All items processed successfully!',
  statistics: {
    workflowDuration: `${Math.round(workflowDuration)}s`,
    totalErrors: errorSummary.total,
    errorsByService: errorSummary.byService,
    retryAttempts: errorSummary.retryStats
  },
  completedAt: new Date().toISOString(),
  lastSuccessfulStep: finalData.lastSuccessfulStep,
  recommendedActions: []
};

// Add recommendations based on error patterns
if (errorSummary.byService.youtubeAPI > 2) {
  completionReport.recommendedActions.push('Consider implementing YouTube API key rotation due to frequent API errors');
}

if (errorSummary.byService.googleSheets > 1) {
  completionReport.recommendedActions.push('Review Google Sheets API quotas and permissions');
}

if (errorSummary.total === 0) {
  completionReport.recommendedActions.push('Workflow completed without errors - excellent!');
}

return [{ json: completionReport }];
